MOV A, #1
MOV B, #2

LOOP:
EQ B, #0
CJP FIM

ADD FLAGS, #2
SUB SP, #2
DIV A, B
LDR M, SP
ADD SP, #2
SUB FLAGS, #2

MOV A, B
MOV B, M
JMP LOOP

FIM:
MOV C, #0x0F
MSL C, [#0 #7]
MSL C, [#0 #5]

GTE A, #10
CJP DESMONTAR
ADD A, #48
STR A, C
ADD C, #1
MOV M, #3
STR M, C

MOV A, #2
MOV B, #0x0F
MSL B, [#1 #4]
MSL B, [#1 #7]
MSL B, [#0 #1]
STR A, B
JMP ENCERRAR          

DESMONTAR:
MOV M, A
ADD FLAGS, #2
SUB SP, #2
DIV M, #10
LDR M, SP
ADD SP, #2
SUB FLAGS, #2

DIV A, #10
ADD A, #48
ADD C, #1
STR A, C

ADD M, #48
ADD C, #1
STR M, C

MOV A, #2
MOV B, #0x0F
MSL B, [#1 #4]
MSL B, [#1 #7]
MSL B, [#0 #1]
STR A, B

ENCERRAR:
ADD FLAGS, #1